mapper.select:
SELECT bno, sv_type, group_origin, admin_type, nickname, title, description, create_time, like_check, dislike_check, view_no, member_no from ( SELECT row_number() over(
ORDER BY bno desc
ORDER BY view_no DESC, bno DESC
ORDER BY like_check DESC, bno DESC
) as num, bno, sv_type, group_origin, admin_type, nickname, title, description, create_time, like_check, dislike_check, view_no, member_no FROM sv_board
sv_type = #{sv_type}
AND ( instr(title, #{search_content}) > 0 OR instr(description, #{search_content}) > 0 )
AND instr(title, #{search_content}) > 0
AND instr(nickname, #{search_content}) > 0
AND create_time BETWEEN curdate()-1 AND curdate()
AND create_time BETWEEN curdate()-7 AND curdate()
AND create_time BETWEEN DATE_ADD(curdate(), INTERVAL 1 MONTH) and curdate()
) a WHERE num BETWEEN #{board_NowStartBno} AND #{board_NowEndBno}

SELECT bno, sv_type, group_origin, admin_type, nickname, title, description, create_time, like_check, dislike_check, view_no, member_no from ( SELECT row_number() over(
ORDER BY bno desc
ORDER BY view_no DESC, bno DESC
ORDER BY like_check DESC, bno DESC
) as num, bno, sv_type, group_origin, admin_type, nickname, title, description, create_time, like_check, dislike_check, view_no, member_no FROM sv_board
sv_type = #{sv_type} AND member_no = #{member_no}
AND ( instr(title, #{search_content}) > 0 OR instr(description, #{search_content}) > 0 )
AND instr(title, #{search_content}) > 0
AND instr(nickname, #{search_content}) > 0
AND create_time BETWEEN curdate()-1 AND curdate()
AND create_time BETWEEN curdate()-7 AND curdate()
AND create_time BETWEEN DATE_ADD(curdate(), INTERVAL 1 MONTH) and curdate()
) a WHERE num BETWEEN #{board_NowStartBno} AND #{board_NowEndBno}

