import Header from "@/components/common/Header.vue";
export default {
  name: "login",
  components: {
    Header
  },
  methods: {
    login_sub: function (e) {
      e.preventDefault();
      if (this.$refs.e_input_id.value.length == 0) {
        alert("아이디를 입력해주세요.");
        console.log(this.$refs.e_input_id.value);
      } else {
        if (this.$refs.e_input_pass.value.length == 0) {
          alert("비밀번호를 입력해주세요.");
        } else {
          this.$axios.post('/member/login', {
            "id": this.$refs.e_input_id.value,
            "pw": this.$refs.e_input_pass.value
          }).then(res => {
            if (res.data == "id") {
              alert('아이디가 틀렸습니다.');
            } else if (res.data == "pw") {
              alert('비밀번호가 틀렸습니다.');
            } else {
              location.href = "/";
            }
          });
        }
      }
    },
    login_keydown: function (e) {
      if (e.keyCode == 13) {
        e.preventDefault();
        this.login_sub();
      }
    }
  }
};
