import Header from "@/components/common/Header.vue";
export default {
  name: "findpass_new",
  components: {
    Header
  },
  created() {
    if (sessionStorage.getItem('user') != null && sessionStorage.getItem('member_findid') == null && sessionStorage.getItem('code_findid') != null) {
      location.href = "/";
    }
  },
  methods: {
    e_input_email_click: function () {
      this.$axios.post('/member/findpass-new', null, {
        params: {
          pass: this.$refs.e_input_code.value,
          pass_re: this.$refs.e_input_code_re.value
        }
      }).then(res => {
        if (res.data == "O") {
          sessionStorage.removeItem("member_findid");
          alert('비밀번호가 변경되었습니다.');
          location.href = '/member/findpass';
        } else if (res.data == "X1") {
          alert('기존 비밀번호와 똑같은 비밀번호입니다.\n다시 입력해주세요.');
        } else if (res.data == "X2") {
          alert('비밀번호가 올바르지 않습니다.\n다시 입력해주세요.');
        } else if (res.data == "X3") {
          alert('비밀번호 재입력이 올바르지 않습니다.\n다시 입력해주세요.');
        }
      });
    }
  }
};
