import Header from "@/components/common/Header.vue";
export default {
  name: "question_detail",
  data: function () {
    return {
      heart_click: null,
      filelist: null,
      s_dto: null,
      user: this.$session.get('user')
    };
  },
  components: {
    Header
  },
  mounted() {
    this.$axios.get('/service/question-detail').then(res => {
      this.heart_click = res.heart_click;
      this.filelist = res.filelist;
      this.s_dto = res.s_dto;
      this.s_dto.description.replace("\r\n", "<br>");
    });
  },
  methods: {
    service_allservice: function () {
      location.href = '/service/allService';
    },
    service_question_member: function () {
      location.href = '/service/question-member';
    },
    service_question_public: function () {
      location.href = '/service/question-public';
    },
    e_like_heart_n_click: function () {
      this.$axios.post('/service/question-detail', {
        e_heart_check: "Y",
        e_bno: this.$axios.e_bno_value.value
      }).then(res => {
        if (res == "O") {
          // 하트 칠하기
          this.$axios.e_like_heart_n.style.display = "none";
          this.$axios.e_like_heart_y.style.display = "block";
          // 숫자 증가
          var heart_num = this.$axios.e_like_num.innerText;
          this.$axios.e_like_num.innerText = parseInt(heart_num) + 1;
        } else {
          alert('하트는 한번만 누를 수 있습니다.');
        }
      });
    },
    e_like_heart_y_click: function () {
      this.$axios.post('/service/question-detail', {
        e_heart_check: "N",
        e_bno: this.$axios.e_bno_value.value
      }).then(res => {
        if (res == "O") {
          // 하트 칠하기
          this.$refs.e_like_heart_n.style.display = "block";
          this.$refs.e_like_heart_y.style.display = "none";
          // 숫자 감소
          var heart_num = this.$axios.e_like_num.innerText;
          this.$refs.e_like_num.innerText = parseInt(heart_num) - 1;
        } else {
          alert('하트는 한번만 누를 수 있습니다.');
        }
      });
    },
    e_btn_delete_click: function (e) {
      var e_btn_delete_form = document.querySelector('#e_btn_delete_form');
      var formData = new FormData(e_btn_delete_form);
      var con_del = confirm('정말 글을 삭제하시겠습니까?');
      if (con_del == true) {
        // 폼 데이터 보내기
        this.$axios.post('/service/question-detail-button', formData).then(res => {
          alert('글이 삭제되었습니다.');
          location.href = res;
        });
      } else {
        e.preventDefault();
      }
    },
    e_btn_fix_click: function (e) {
      var e_btn_fix_form = document.querySelector('#e_btn_fix_form');
      var formData = new FormData(e_btn_fix_form);
      var con_fix = confirm('정말 글을 수정하시겠습니까?');
      if (con_fix == true) {
        // 폼 데이터 보내기
        this.$axios.post('/service/question-detail-button', formData).then(res => {
          alert('글이 수정되었습니다.');
          location.href = res;
        });
      } else {
        e.preventDefault();
      }
    }
  }
};
