import Header from "@/components/common/Header.vue";
export default {
  name: "question_guide_search",
  data: function () {
    return {
      s_page: null,
      s_dto_list: null,
      s_searchdto: null,
      search_content_: null,
      user: JSON.parse(sessionStorage.getItem('user'))
    };
  },
  components: {
    Header
  },
  mounted() {
    this.$axios.get('/service/question-guide-search').then(res => {
      this.s_page = res.data.s_page;
      this.s_dto_list = res.data.s_dto_list;
      this.s_searchdto = res.data.s_searchdto;
      // 페이지 값
      for (var i = 1; i < this.s_page.page_EndBno - this.s_page.page_StartBno + 1; i++) {
        if (i == this.s_page.page_NowBno) {
          this.$refs.e_paging_num.innerHTML += '<a id="page_NowBno">' + i + '</a>';
        } else {
          this.$refs.e_paging_num.innerHTML += '<a ' + 'href=' + '"/service/question-guide-search?page_NowBno=' + i +
          // eslint-disable-next-line
          '&search_time=' + s_searchdto.search_time
          // eslint-disable-next-line
          + '&search_type=' + s_searchdto.search_type
          // eslint-disable-next-line
          + '&search_content=' + s_searchdto.search_type + '"' + 'class="page_Bno">' + i + '</a>';
        }
      }
      // 검색 값
      // eslint-disable-next-line
      var search_time = this.s_searchdto.search_time;
      var search_type = this.s_searchdto.search_type;
      document.getElementById(search_time).setAttribute('selected', 'selected');
      document.getElementById(search_type).setAttribute('selected', 'selected');
      // eslint-disable-next-line
      search_content_ = URLEncoder.encode(this.s_searchdto.search_content, "UTF-8");
    });
  },
  methods: {
    service_allservice: function () {
      location.href = '/service/allService';
    },
    service_question_member: function () {
      location.href = '/service/question-member';
    },
    service_question_guide: function () {
      location.href = '/service/question-guide';
    },
    service_question_write: function () {
      location.href = '/service/question-write';
    },
    service_question_public: function () {
      location.href = '/service/question-public';
    },
    service_question_guide_search(page_NowBno, search_time, search_type, search_content) {
      var url = '/service/question-guide-search?page_NowBno=' + page_NowBno + '&search_time=' + search_time + '}&search_type=' + search_type + '&search_content=' + search_content;
      location.href = url;
    },
    // 글 상세 페이지
    blist_title_click: function (e) {
      var val = e.target.parentElement.firstElementChild.value;
      this.$refs.e_bno_val.value = val;
      var url = '/service/question-detail?bno=' + val;
      location.href = url;
    },
    // 검색 결과 - 엔터
    s_content_input_keydown: function (e) {
      if (e.keyCode == 13) {
        e.preventDefault();
        this.$refs.s_content_btn.click();
      }
    },
    // 검색 결과 전달
    s_content_btn_click: function () {
      // 검색 기간 등 정보
      let e_search_time = this.$refs.e_search_time_sel.value;
      let e_search_type = this.$refs.e_search_type_sel.value;
      let e_search_content = this.$refs.s_content_input.value;
      let url = "/service/question-guide-search";
      url += "?search_time=" + e_search_time;
      url += "&search_type=" + e_search_type;
      url += "&search_content=" + e_search_content;
      location.href = url;
    }
  }
};
