import Header from "@/components/common/Header.vue";
export default {
  name: "login",
  components: {
    Header
  },
  created() {
    if (sessionStorage.getItem('user') != null) {
      location.href = "/";
    }
  },
  methods: {
    login_sub: function (e) {
      e.preventDefault();
      if (this.$refs.e_input_id.value.length == 0) {
        alert("아이디를 입력해주세요.");
      } else {
        if (this.$refs.e_input_pass.value.length == 0) {
          alert("비밀번호를 입력해주세요.");
        } else {
          this.$axios.post('/member/login', null, {
            params: {
              id: this.$refs.e_input_id.value,
              pw: this.$refs.e_input_pass.value
            }
          }).then(res => {
            if (res.data == null || res.data == "") {
              alert('아이디 혹은 비밀번호가 틀렸습니다.');
            } else {
              sessionStorage.setItem('user', JSON.stringify(res.data));
              alert('로그인 되었습니다.');
              location.href = "/";
            }
          });
        }
      }
    },
    login_keydown: function (e) {
      if (e.keyCode == 13) {
        e.preventDefault();
        this.login_sub();
      }
    }
  }
};
